body {
  font-family: sans-serif;
}

/* Box styling */
.box {
  width: 300px;
  padding: 20px;
  margin: 30px;
  border: 2px solid #3498db;
  background-color: #ecf0f1;
}

/* Header styling */
#main-header {
  background: #34495e;
  color: white;
  padding: 20px;
}

/* Info text paragraphs */
.info-text {
  color: #555;
  font-size: 16px;
}

/* Highlighted paragraph */
#highlight {
  background: #f39c12;
  padding: 15px;
}
